import React, { Component, Fragment} from 'react';
import { Link, Route } from 'react-router-dom'
import logo from './logo.svg';
import './App.css';
import Company from './Components/Company.js'
import Header from './Components/Header.js'
import Rides from './Components/Rides.js'
import ForumsPage from './Components/ForumsPage.js'
import Profile from './Components/Profile.js'

// function Forum() {
//   return <h2> Forums </h2>
// }


class App extends Component {

  state = {
    modal: false,
    allCompanies:[],
    users: "",
    // users: [],
    rides: [],
    forums:[],
    allForums:[],
    friendships: [],
    currentUser: ''
  }
  //
  // getCompanies(){
  //   let companies = [...this.state.companies]
  //   console.log(companies)
  //   return companies.map(company=>{
  //     console.log('in fetch,  company:',company)
  //     return <Company company={company}/>
  //   })
  // }

  componentDidMount(){
    const userUrl = 'http://localhost:3000/api/v1/users'
    fetch(userUrl)
    .then(res=>res.json())
    .then(users=>{
      this.setState({
        users: users,

        currentUser: users.find(user=> user.name === "Zev")

      })
    })

    const companyUrl = 'http://localhost:3000/api/v1/companies'
    fetch(companyUrl)
    .then(res=>res.json())
    .then(companies=>{

      this.setState({
        allCompanies: companies
      })
    })
    const rideUrl = 'http://localhost:3000/api/v1/rides'
    fetch(rideUrl)
    .then(res=>res.json())
    .then(rides=>{

      this.setState({
        rides
      })
    })
    const forumUrl = 'http://localhost:3000/api/v1/forums'
    fetch(forumUrl)
    .then(res=>res.json())
    .then(allForums=>{

      this.setState({
        allForums
      })
    })
  }

  renderProfileLink = () => {
    if(this.state.currentUser){
      return <Link to={`/profile/${this.state.currentUser.id}`}>Profile</Link>
    }
  }




  render() {
    console.log('in app render', this.state)
    return (
      <Fragment>
        <Header />
        <div className="container col-11">
          {this.renderProfileLink()}
          <Link to="/rides">Rides</Link>
          <Link to="/forums">Forums</Link>
        </div>



          <Route path="/profile/:id" exact render={(props) => {
              // debugger
              let id = parseInt(props.match.params.id)
              let userUrl = 'http://localhost:3000/api/v1/users'
            if(id === this.state.currentUser.id){

              let user = this.state.users.find(u => u.id === id )
              // debugger

              // debugger
              return (
                <Profile
                  {...props}
                  allCompanies={this.state.allCompanies}
                  currentUser={this.state.currentUser}
                  user={user}
                />
              )
            }
          }}/>


          <Route path="/rides" exact render={() => {
            return (
              <Fragment>
              <Rides
                allUsers={this.state.users}
                user={this.state.currentUser}
                forum={this.state.forums}
                rides={this.state.rides}
                allCompanies={this.state.allCompanies}
              />
              </Fragment>
            )}

            }/>
          {/* WE ONLY DOING THE FISRT USER FOR NOW, K?*/}
            <Route path="/" exact render={() => {
              return (
                <Fragment>
                <Rides
                  allUsers={this.state.users}
                  users={this.state.users}
                  user={this.state.currentUser}
                  forum={this.state.forums}
                  rides={this.state.rides}
                  allCompanies={this.state.allCompanies}
                />
                </Fragment>
              )}

            }/>


      </Fragment>
    );
  }
}

export default App;




