  constructor(props){
    super(props)
    this.state = {
      allRides: this.props.rides,
      filteredRides: this.props.rides
    }
  }

  componentWillReceiveProps(){
    this.setState({
      allRides: this.props.rides,
      filteredRides: this.props.rides

    })
  }

  handleForumClick=(e)=>{
    console.log('in handleforum click',e)
  }

  render() {
    console.log("hit forums route")
