import React,{Fragment} from "react"


class Leaderboard extends React.Component {

  constructor(props){
    super(props)
  }
  getTotalMiles=(user)=>{
    let totalMiles=0
    if(user.rides){
      user.rides.map(r=>totalMiles+=r.distance)
    }
    return totalMiles
  }
  getTotalEarned=(user)=>{
    let totalEarned=0
    if(user.rides){
      user.rides.map(r=>totalEarned+=r.price)
    }
    return totalEarned
  }

  getTotalTimeDriving=(user)=>{
    let totalTime =0
    if(user.rides){
      user.rides.map(r=>{
        let rideTime = r.end_at-r.started_at
        totalTime+=rideTime
      })
    }
    return totalTime
  }
  renderLeader=()=>{
    if(this.props.leaders){
      return this.props.leaders.map(leader=>{
        return (
          <Fragment>
            <h1>User:{leader.name}</h1>
            <p>Miles: {this.getTotalMiles(leader)}</p>
            <p>Total Earned:{this.getTotalEarned(leader)}</p>
            <p>Most Time:{this.getTotalTimeDriving(leader)}</p>
          </Fragment>
        )

      })
    }
  }

  render() {
    return (
      <div>
        {this.renderLeader()}

      </div>
    )
  }
}
export default Leaderboard
