import React,{Fragment} from "react"


class Leaderboard extends React.Component {

  constructor(props){
    super(props)
  }
  getTotalMiles=(user)=>{
    let totalMiles=0
    if(user.rides){
      user.rides.map(r=>totalMiles+=r.distance)
      return totalMiles
    }
  }
  getTotalEarned=(user)=>{
    let totalEarned=0
    console.log('get total earnd',user)
    if(user.rides){
      user.rides.map(r=>totalEarned+=r.price)
      return totalEarned
    }
  }

  getTotalTimeDriving=(user)=>{
    let totalTime =0
    if(user.rides){
      user.rides.map(r=>{
        let rideTime = r.end_at-r.started_at
        totalTime+=rideTime
      })
      return totalTime
    }
  }
  getMileLeader=(users)=>{
    let newUsers=[...users]
    if(newUsers!==[]){
      newUsers.sort((usera,userb)=>{
        return this.getTotalMiles(usera)-this.getTotalMiles(userb)
      })
      console.log('mile leaders',users.reverse()[0])

      return newUsers.reverse()[0]
    }
  }
  getRatingLeader=(users)=>{
    let newUsers=[...users]
    if(newUsers!==[]){
      newUsers.sort((usera,userb)=>{
        return usera.rating-userb.rating
      })
    }
    return newUsers.reverse()[0]
  }
  getExperienceLeader=(users)=>{
    let newUsers=[...users]
    if(newUsers!==[]){
      newUsers.sort((usera,userb)=>{
        return usera.experience-userb.experience
      })
    }
    return newUsers.reverse()[0]
  }

  getTimeLeader=(users)=>{
    if(users!==[]){
      return users.sort((usera,userb)=>{
        return this.getTimeLeader(usera)-this.getTimeLeader(userb)
      })
    }

  }
  getEarnerLeader=(users)=>{
    let newUsers=[...users]
    if(newUsers!==[]){
      newUsers.sort((usera,userb)=>{
        // console.log('user a',usera)
        // console.log('user b',userb)

        console.log('user a  earned',this.getTotalEarned(usera))
        console.log('user b earned',this.getTotalEarned(userb))

        return this.getTotalEarned(usera)-this.getTotalEarned(userb)
      })

      return newUsers.reverse()[0]
    }
  }
  renderUserTotals=()=>{
    if(this.props.leaders){
      return this.props.leaders.map((leader,idx)=>{

        return (
          <div key={idx}>

            <h1>User:{leader.name}</h1>
            <p>Miles: {this.getTotalMiles(leader)}</p>
            <p>Total Earned:{this.getTotalEarned(leader)}</p>
            <p>Total Time:{this.getTotalTimeDriving(leader)}</p>
          </div>
        )

      })
    }
  }
  renderLeaderBoard=()=>{
    // console.log('mile leader',this.getMileLeader(this.props.leaders))
    // console.log('Miles:',this.getTotalMiles())

    return (
      <Fragment>
        <div class="container">
      <div class="row">
          <div class="col-md-9">
              <div class="list-group">
                  <a class="list-group-item google-plus">
                      <h3 class="pull-right">
                          <i class="icon glyphicon glyphicon-fire"></i>
                      </h3>
                      <h4 class="list-group-item-heading count">Highest Rated:{this.getRatingLeader(this.props.leaders)?
                        this.getRatingLeader(this.props.leaders).name:null}</h4>
                      <p class="list-group-item-text">{this.getRatingLeader(this.props.leaders)?
                        (this.getRatingLeader(this.props.leaders).rating):null}</p>
                  </a>
                  <a class="list-group-item visitor">
                      <h3 class="pull-right">
                          <i class="icon glyphicon glyphicon-time"></i>
                      </h3>
                      <h4 class="list-group-item-heading count">
                          1000</h4>
                      <p class="list-group-item-text">Duration</p>
                  </a>
                  <a class="list-group-item facebook-like">
                      <h3 class="pull-right">
                          <i class="icon glyphicon glyphicon-road"></i>
                      </h3>
                      <h4 class="list-group-item-heading count">
                      Most Miles: {this.getMileLeader(this.props.leaders)?
                        this.getMileLeader(this.props.leaders).name:null}</h4>
                      <p class="list-group-item-text">{this.getMileLeader(this.props.leaders)?
                          this.getTotalMiles(this.getMileLeader(this.props.leaders)):null}</p>
                  </a>
                  <a class="list-group-item facebook-like">
                      <h3 class="pull-right">
                          <i class="icon glyphicon glyphicon-road"></i>
                      </h3>
                      <h4 class="list-group-item-heading count">
                      Most Earned:{this.getEarnerLeader(this.props.leaders)?
                        this.getEarnerLeader(this.props.leaders).name:null}</h4>
                      <p class="list-group-item-text">${this.getEarnerLeader(this.props.leaders)?
                       this.getTotalEarned(this.getEarnerLeader(this.props.leaders)):null}</p>
                  </a>
                  <a class="list-group-item facebook-like">
                      <h3 class="pull-right">
                          <i class="icon glyphicon glyphicon-road"></i>
                      </h3>
                      <h4 class="list-group-item-heading count">
                      Experienced:{this.getExperienceLeader(this.props.leaders)?
                        this.getExperienceLeader(this.props.leaders).name:null}</h4>
                      <p class="list-group-item-text">{this.getExperienceLeader(this.props.leaders)?
                        this.getExperienceLeader(this.props.leaders).experience:null}</p>
                  </a>
              </div>
          </div>

      </div>
  </div>

        {/*
          <ul className="list-group list-group-flush">Most Time Spent:{this.getTimeLeader(this.props.leaders)?
          this.getTimeLeader(this.props.leaders).name:null}</p>
        */}
      </Fragment>
    )

  }

  render() {
    return (
      <div className="leaderboard-card" className="card">
        <ul className="list-group list-group-flush">

        {this.renderLeaderBoard()}
        {/*this.renderUserTotals()*/}
        </ul>

      </div>
    )
  }
}
export default Leaderboard
