import React,{Fragment} from "react";
import ProfileCard from './ProfileCard'
import RideList from './RideList.js'
import ForumsList from './ForumsList.js'
import ReactModal from 'react-modal'

class Profile extends React.Component {


  state={
    modal:false,
    name:this.props.user.name,
    companies:this.props.user.companies,
    location:this.props.user.location,
    rating:this.props.user.rating,
    id:this.props.user.id

  }



  handleEdit = (e) => {
    console.log(e)
    this.setState({modal:true})
    // this.renderEditModal()
  }
  handleEditFormChange=(e)=>{
    console.log(e.target.value)
    console.log(e.target.name)
    let value = e.target.value;
    let name = e.target.name;

    this.setState({
      [name]: value
    })


  }
  handleAfterOpen=()=>{
    console.log('opened')
  }
  handleAfterClose=()=>{
    console.log('closed')
    this.setState({modal:false})
  }
  handleSubmit=(e)=>{
    e.preventDefault()
    console.log('in save edit user',user)
    this.setState({modal:false})

    this.patchEditProfile()

  }

  patchEditProfile(){
    const profileUrl=`http://localhost:3000/api/v1/users/${this.state.id}`
    fetch(profileUrl,{
      method:'PATCH',
      body:JSON.stringify({
        name:this.state.name,

      })
    })
  }
  renderEditModal(){
    //make modal smaller
    return (
      <ReactModal
        isOpen={this.state.modal}
        onAfterOpen={this.handleAfterOpen}
        onRequestClose={this.handleAfterClose}
        shouldCloseOnEsc={true}
        shouldCloseOnOverlayClick={true}
        ariaHideApp={false}
        data={{
          background: "green"
        }}

        contentLabel="example modal"
        >

        <form>
          <input name="name" type="text" onChange={(e)=>this.handleEditFormChange(e)} value={this.props.user.name}></input><br/>
          <input name="experience" type="text" onChange={(e)=>this.handleEditFormChange(e)} value={this.props.user.experience}></input><br/>
          <input name="rating" type="text" onChange={(e)=>this.handleEditFormChange(e)} value={this.props.user.rating}></input><br/>
          <input name="car" type="text" onChange={(e)=>this.handleEditFormChange(e)} value={this.props.user.car}></input><br/>
          <input name="location" type="text" onChange={(e)=>this.handleEditFormChange(e)} value={this.props.user.location}></input><br/>
          <button type="submit" onClick={(e)=>this.handleSubmit(e)}>Save Edit</button>
        </form>
        <button onClick={this.handleAfterClose}>Close Modal</button>


      </ReactModal>
    )
  }



  render() {

    // console.log("hit profile route", this.props)

    return (
      <Fragment>
        <div className = "container col-11">
          <div className="row justify-content-center">
            <div className="col-3 justify-content-center" id="profile-card-container">
              <ProfileCard user={this.props.user}/>
              <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter" id="edit-profile" onClick={this.handleEdit}> Edit Profile </button>
{/****************************************************************/}
              {this.renderEditModal()}



            {/****************************************************************/}

            </div>


            <div className="col" id="profile-rides-list">
              <RideList rides={this.props.user.rides}/>
              <ForumsList forums={this.props.user.forums}/>
            </div>
          </div>
        </div>
      </Fragment>
    )
  }
}
export default Profile
