import React, { Component, Fragment} from 'react';
import { Link, Route } from 'react-router-dom'
import logo from './logo.svg';
import './App.css';
import Company from './Components/Company.js'
import Header from './Components/Header.js'
// import Ride from './Components/Header.js'

function Forum() {
  return <h2> Forums </h2>
}

function Ride() {
  console.log("hit rides route")
  return <h2> Rides </h2>
}


class App extends Component {

  state={
    companies:[]
  }

  getCompanies(){
    let companies = [...this.state.companies]
    console.log(companies)
    return companies.map(company=>{
      console.log('in fetch,  company:',company)
      return <Company company={company}/>
    })
  }

  componentDidMount(){
    const companyUrl = 'http://localhost:3000/api/v1/companies'
    fetch(companyUrl)
    .then(res=>res.json())
    .then(companies=>{
      this.setState({
        companies: companies
      })
    })
  }


  render() {
    console.log('in app render')
    return (
      <Fragment>
        <Header />
        <Link to="/rides">Ride Feed</Link>
        <Link to="/">Home</Link>
        <Link to="/profile">Profile</Link>

        <Route path="/rides" exact component={Ride} />
        <Route path="/" exact component={Forum} />
        <div className="App">
          {this.getCompanies()}
        </div>
      </Fragment>
    );
  }
}

export default App;
