import React,{Fragment} from "react"

const Leaderboard = (props) => {
  function getTotalMiles(user){
    let totalMiles=0
    if(user.rides){
      user.rides.map(r=>totalMiles+=r.distance)
    }
    return totalMiles
  }
  const getTotalEarned=(user)=>{
    let totalEarned=0
    if(user.rides){
      user.rides.map(r=>totalEarned+=r.price)
    }
    return 0
  }
  function getTotalTimeDriving(user){
    let totalTime =0
    if(user.rides){
      user.rides.map(r=>{
        let rideTime = r.end_at-r.started_at
        totalTime+=rideTime
      })
    }
    return totalTime
  }
  const renderLeader=()=>{
    if(props.leaders){
      return props.leaders.map(leader=>{
        return (
          <Fragment>
            <p>Miles: {getTotalMiles(leader)}</p>
            <p>Total Earned:{getTotalEarned(leader)}</p>
            <p>Most Time:{totalTimeDriving(leader)}</p>
          </Fragment>
        )

      })
    }
  }
  return (
  <div className="leaderboard">
    {renderLeader()}

  </div>
  )
}

export default Leaderboard
