import React,{Fragment} from "react";
import ProfileCard from './ProfileCard'
import RideList from './RideList.js'
import ForumsList from './ForumsList.js'
import ReactModal from 'react-modal'

class Profile extends React.Component {


  state={
    modal:false
  }



  handleEdit = (e) => {
    console.log(e)
    this.setState({modal:true})
    // this.renderEditModal()
  }
  handleEditFormChange=()=>{

  }

  postEditProfile(editProfile){
    const profileUrl=`http://localhost:3000/api/v1/users/${editProfile.id}`
    fetch(profileUrl,{
      method:'POST',
      body:JSON.stringify({
        editProfile
      })
    })
  }
  renderEditModal(){
    //make modal smaller
    return (
      <ReactModal
        isOpen={this.state.modal}
        onAfterOpen={this.handleAfterOpen}
        onRequestClose={this.handleAfterClose}
        shouldCloseOnEsc={true}
        shouldCloseOnOverlayClick={true}
        ariaHideApp={false}
        data={{
          background: "green"
        }}

        contentLabel="example modal"
        >

        <form>
          <input type="text" onChange={this.handleEditFormChange} value={this.props.user.name}></input>{"\n"}
          <input type="text" onChange={this.handleEditFormChange} value={this.props.user.experience}></input>{"\n"}
          <input type="text" onChange={this.handleEditFormChange} value={this.props.user.rating}></input>
          <input type="text" onChange={this.handleEditFormChange} value={this.props.user.car}></input><br/>
          <input type="text" onChange={this.handleEditFormChange} value={this.props.user.location}></input><br/>
          <button type="submit" onClick={this.saveEdit}>Save Edit</button>
        </form>
        <button onClick={this.handleAfterClose}>Close Modal</button>


      </ReactModal>
    )
  }
  handleAfterOpen=()=>{
    console.log('opened')
  }
  handleAfterClose=()=>{
    console.log('closed')
    this.setState({modal:false})
  }
  saveEdit=()=>{
    console.log('in save edit')
  }


  render() {

    // console.log("hit profile route", this.props)

    return (
      <Fragment>
        <div className = "container col-11">
          <div className="row justify-content-center">
            <div className="col-3 justify-content-center" id="profile-card-container">
              <ProfileCard user={this.props.user}/>
              <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter" id="edit-profile" onClick={this.handleEdit}> Edit Profile </button>
{/****************************************************************/}
              {this.renderEditModal()}



            {/****************************************************************/}

            </div>


            <div className="col" id="profile-rides-list">
              <RideList rides={this.props.user.rides}/>
              <ForumsList forums={this.props.user.forums}/>
            </div>
          </div>
        </div>
      </Fragment>
    )
  }
}
export default Profile
