import React,{Fragment} from "react"


class Leaderboard extends React.Component {

  constructor(props){
    super(props)
  }
  getTotalMiles=(user)=>{
    let totalMiles=0
    if(user.rides){
      user.rides.map(r=>totalMiles+=r.distance)
      return totalMiles
    }
  }
  getTotalEarned=(user)=>{
    let totalEarned=0
    if(user.rides){
      user.rides.map(r=>totalEarned+=r.price)
      return totalEarned
    }
  }

  getTotalTimeDriving=(user)=>{
    let totalTime =0
    if(user.rides){
      user.rides.map(r=>{
        let rideTime = r.end_at-r.started_at
        totalTime+=rideTime
      })
      return totalTime
    }
  }
  getMileLeader=(users)=>{
    if(users!==[]){
      return users.sort((usera,userb)=>{

        return this.getTotalMiles(usera)-this.getTotalMiles(userb)
      })
    }
  }
  getTimeLeader=(users)=>{
    if(users!==[]){
      return users.sort((usera,userb)=>{
        return this.getTimeLeader(usera)-this.getTimeLeader(userb)
      })
    }

  }
  getEarnerLeader=(users)=>{
    if(users!==[]){
      return users.sort((usera,userb)=>{
        // console.log('user a',usera)
        // console.log('user b',userb)

        console.log('user a  earned',this.getTotalEarned(usera))
        console.log('user b earned',this.getTotalEarned(userb))

        return this.getTotalEarned(usera)-this.getTotalEarned(userb)
      })
      // debugger
      // return sortedLeaders
    }

  }
  renderLeader=()=>{
    if(this.props.leaders){
      return this.props.leaders.map(leader,key=>{

        return (
          <Fragment>
            <h1>User:{leader.name}</h1>
            <p>Miles: {this.getTotalMiles(leader)}</p>
            <p>Total Earned:{this.getTotalEarned(leader)}</p>
            <p>Total Time:{this.getTotalTimeDriving(leader)}</p>
          </Fragment>
        )

      })
    }
  }
  renderLeaderBoard=()=>{

    return (
      <Fragment>
        <p>Most Miles: {this.getMileLeader(this.props.leaders).name}</p>
        <p>Most Earned:{this.getEarnerLeader(this.props.leaders).name}</p>
        {/*<p>Most Time:{this.getTimeLeader(this.props.leaders).name}</p>*/}
      </Fragment>
    )

  }

  render() {
    return (
      <div>
        {this.renderLeader()}
        {this.renderLeaderBoard()}

      </div>
    )
  }
}
export default Leaderboard
