import React, {Fragment} from "react";
import ForumsList from './ForumsList.js'
import ChatBox from './ChatBox'

class Forums extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      allForums: null,
      clickedForum: null,
      openChat: true
    }
  }

  componentDidMount(){
    fetch('http://localhost:3000/api/v1/forums')
    .then(r => r.json())
    .then(r => {
      this.setState({allForums: r})
    })
  }

  handleForumClick = (e) => {
    this.setState({clickedForum: e.target.id})
    this.setState({openChat: false})
  }




  render(){
    return (
      <div>
      {this.state.openChat ? <ForumsList handleForumClick={this.handleForumClick} forums={this.state.allForums}/> : <ChatBox users={this.props.users}currentChat={this.state.allForums.find(forum => forum.topic === this.state.clickedForum)}/>}
      </div>
    )
  }

}
export default Forums
